import 'package:flutter/material.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Lista de contatos',
      theme: ThemeData(
        primarySwatch: Colors.blue,
      ),
      home: ContactsListScreen(),
    );
  }
}

class ContactsListScreen extends StatefulWidget {
  @override
  _ContactsListScreenState createState() => _ContactsListScreenState();
}

class _ContactsListScreenState extends State<ContactsListScreen> {
  List<Contact> _contacts = [
    Contact(
        name: 'Dfideliz',
        email: 'dfideliz.recayd@gmail.com',
        image:
            'data:image/jpeg;base64,',
        isFavorite: false),
    Contact(
        name: 'MC Mãozinha',
        email: 'mc.maozinha@gmail.com',
        image:
            'data:image/jpeg;base64,',
        isFavorite: true),
    Contact(
        name: 'MC IG',
        email: 'ninguem.tapuro@gmail.com',
        image:
            'https://pbs.twimg.com/media/FigSo6gXEAAXIfq?format=jpg&name=900x900',
        isFavorite: false),
    Contact(
        name: 'MC FAFA',
        email: 'chan.chan@gmail.com',
        image:
            'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSwCnzJlGpuhVy16yuqV967QBhc69heRlpuzw&usqp=CAU',
        isFavorite: true),
  ];

  @override
  Widget build(BuildContext context) {
    int favoriteContactsCount = _contacts.where((c) => c.isFavorite).length;

    return Scaffold(
      appBar: AppBar(
        title: Text('Lista de contatos'),
      ),
      body: ListView.builder(
        itemCount: _contacts.length,
        itemBuilder: (BuildContext context, int index) {
          Contact contact = _contacts[index];
          return ListTile(
            leading: CircleAvatar(
                backgroundImage: NetworkImage(_contacts[index].image)),
            title: Text(contact.name),
            subtitle: Text(_contacts[index].email),
            trailing: IconButton(
              icon: Icon(
                contact.isFavorite ? Icons.star : Icons.star_border,
                color: contact.isFavorite ? Colors.yellow : null,
              ),
              onPressed: () {
                setState(() {
                  contact.isFavorite = !contact.isFavorite;
                });
              },
            ),
          );
        },
      ),
      bottomNavigationBar: BottomAppBar(
        child: Container(
          height: 50.0,
          child: Center(
            child: Text(
              '$favoriteContactsCount contatos favoritos',
              style: TextStyle(
                fontWeight: FontWeight.bold,
                fontSize: 18.0,
              ),
            ),
          ),
        ),
      ),
    );
  }
}

class Contact {
  String name;
  bool isFavorite;
  String email;
  String image;

  Contact(
      {required this.name,
      required this.isFavorite,
      required this.email,
      required this.image});
}
